import React from "react";
import { Link, useParams } from "react-router-dom";

export default ({ name, description, balance, id, goal }) => {
  let params = useParams();

  return (
    <div className="ui card">
      <div className="content">
        <div className="header">{name}</div>
        <i>
          <div className="header">{description}</div>
        </i>
        <div className="right floated content">
          {/* <Link
              to={{
                pathname: `/squares/delete/${props.id}`,
                state: {
                  id: props.id,
                },
              }}
            > */}
          <i
            style={{ fontSize: "1.5rem" }}
            className="trash alternate icon"
          ></i>
          {/* </Link> */}
          <Link
            to={{
              pathname: `/user/${params.id}/squares/${id}/edit`,
              state: { name, description, goal, id },
            }}
          >
            <i style={{ fontSize: "1.5rem" }} className="edit icon"></i>
          </Link>
        </div>
        <div className="description">
          <p>Balance: ${balance}</p>
          <p>Goal: ${goal}</p>
          {/* <Link
              to={{
                pathname: "/squares/deposit",
                state: {
                  squareType: props.squareType,
                  id: props.id,
                },
              }}
            > */}
          <i className="plus icon" /> Add to Square
          {/* </Link> */}
        </div>
      </div>
    </div>
  );
};
