import React, { useState } from "react";
import { useHistory, useParams } from "react-router-dom";
import { useMutation } from "@apollo/client";

import { CREATE_SQUARE } from "../queries/mutations";

export default () => {
  const [name, setName] = useState("");
  const [goal, setGoal] = useState(0);
  const [description, setDescription] = useState("");
  const [createSquare] = useMutation(CREATE_SQUARE);
  let history = useHistory();
  let params = useParams();

  const submitSquare = () => {
    createSquare({ variables: { name, goal, description } });
  };

  return (
    <div className="ui centered card">
      <form className="ui form segment">
        <p>Let's make a square!</p>
        <div className="two fields">
          <div className="field">
            <label>Name</label>
            <input
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="What's it called?"
              name="name"
              type="text"
            />
          </div>
          <div className="field">
            <label>Goal</label>
            <input
              placeholder="How much you wanna save?"
              name="goal"
              type="number"
              value={goal}
              onChange={(e) => setGoal(e.target.valueAsNumber)}
            />
          </div>
        </div>
        <div className="field">
          <label>Description</label>
          <textarea
            placeholder="Tell us what that $$ will be used for..."
            name="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="ui primary submit button">Submit</div>
        <div
          onClick={() => history.goBack()}
          className="ui negative submit button"
        >
          Exit
        </div>
      </form>
    </div>
  );
};
