import React from "react";
import { useQuery } from "@apollo/client";

import { FETCH_USERS } from "../queries/queries";

const Home = () => {
  const { loading, error, data, refetch } = useQuery(FETCH_USERS);

  if (loading) return <h1>Loading...</h1>;
  if (error) return <h1>{error.message}</h1>;

  const renderUsers = () =>
    data.users.map((user) => {
      return (
        <div class="item">
          <div class="right floated content">
            <div class="ui button">View Account</div>
          </div>
          <div className="ui avatar image">
            <i className="user outline circle icon" />
          </div>
          <div class="content">
            {user.name} {`      `} {user.age}
          </div>
        </div>
      );
    });

  return <div class="ui middle aligned divided list">{renderUsers()}</div>;
};

export default Home;
