import React from "react";
import { useQuery } from "@apollo/client";
import { useParams } from "react-router-dom";

import { FETCH_USER } from "../queries/queries";

export default () => {
  let params = useParams();
  const { id } = params;

  const { loading, error, data, refetch } = useQuery(FETCH_USER, {
    variables: { id },
  });

  if (loading) return <h1>Loading...</h1>;
  if (error) return <h1>{error.message}</h1>;

  console.log(data);

  const { age, balance, name, squares } = data.user;

  return (
    <div class="ui centered card">
      <div class="content">
        <div class="header">{name}'s Account</div>
        <div class="description">
          <p>Balance: ${balance}</p>
          <p>
            {/* {props.balance === 0
              ? "Would you like to add more money to your account?"
              : ""} */}
          </p>
        </div>
      </div>
      <div class=" left floated content">
        {/* <Link
          to={{
            pathname: "/squares/deposit",
            state: {
              mainAccType: props.mainAccType,
            },
          }}
        > */}
        <i class="plus icon" />
        Add to balance
        {/* </Link> */}
      </div>
    </div>
  );
};
