import React from "react";
import { useQuery } from "@apollo/client";
import { useParams, Link } from "react-router-dom";

import { FETCH_USER } from "../queries/queries";
import Square from "./Square";

export default () => {
  let params = useParams();
  const { id } = params;

  const { loading, error, data, refetch } = useQuery(FETCH_USER, {
    variables: { id },
  });

  if (loading) return <h1>Loading...</h1>;
  if (error) return <h1>{error.message}</h1>;

  const { balance, name, squares } = data.user;

  const renderSquares = () => {
    return squares.map((sq) => {
      const { balance, description, goal, id, name } = sq;
      return (
        <Square
          balance={balance}
          description={description}
          goal={goal}
          id={id}
          name={name}
        />
      );
    });
  };

  refetch();

  return (
    <div>
      <div class="ui centered card">
        <div class="content">
          <div class="header">{name}'s Account</div>
          <div class="description">
            <p>Balance: ${balance}</p>
            <p>
              {balance === 0
                ? "Would you like to add more money to your account?"
                : ""}
            </p>
          </div>
        </div>
        <div class=" left floated content">
          <Link to={`/user/${id}/deposit`}>
            <i class="plus icon" />
            Add to balance
          </Link>
        </div>
      </div>
      <div className="four wide column">{renderSquares()}</div>
      <Link to={`/user/${id}/squares/new`}>
        <div
          style={{
            position: "absolute",
            bottom: "2rem",
            right: "2rem",
            fontSize: "3rem",
          }}
        >
          <i className="plus icon"></i>
        </div>
      </Link>
    </div>
  );
};
